//<![CDATA[

var div = document.createElement("img");

//displays either lit up bolt or dark bolt depending on whether device is plugged in
function updateBolt(){

	if(isCharging === 1) {
		//show dim bolt
		div.src = 'data:image/svg+xml;base64,';
		div.style.width = '12px';

	}else {
		//show full bolt
		div.src = 'data:image/svg+xml;base64,';
		div.style.width = '12px';

	}
	//attaches picture to element
	document.getElementById('bolt').appendChild(div);

	//displays battery percentage
	document.getElementById('battery').innerHTML = "" + batteryPercent + "%";
}

//]]>
